import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';
import toast from 'react-hot-toast';
import { 
  ArrowLeft, 
  Star, 
  Lightbulb, 
  User, 
  Mail, 
  Phone, 
  MapPin,
  CheckCircle,
  AlertCircle,
  TrendingUp
} from 'lucide-react';

const ResumeAnalysis = () => {
  const { id } = useParams();
  const [resume, setResume] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchResumeAnalysis();
  }, [id]);

  const fetchResumeAnalysis = async () => {
    try {
      const response = await axios.get(`/api/resume/${id}`);
      setResume(response.data);
    } catch (error) {
      toast.error('Failed to fetch resume analysis');
    } finally {
      setLoading(false);
    }
  };

  const getScoreColor = (score) => {
    if (score >= 80) return 'text-green-600';
    if (score >= 60) return 'text-yellow-600';
    return 'text-red-600';
  };

  const getScoreBgColor = (score) => {
    if (score >= 80) return 'bg-green-100';
    if (score >= 60) return 'bg-yellow-100';
    return 'bg-red-100';
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  if (!resume) {
    return (
      <div className="text-center py-12">
        <h2 className="text-2xl font-bold text-secondary-900 mb-4">
          Resume not found
        </h2>
        <Link to="/dashboard" className="btn-primary">
          <ArrowLeft className="w-4 h-4 mr-2" />
          Back to Dashboard
        </Link>
      </div>
    );
  }

  return (
    <div className="max-w-6xl mx-auto">
      {/* Header */}
      <div className="mb-8">
        <Link 
          to="/dashboard" 
          className="inline-flex items-center text-secondary-600 hover:text-primary-600 mb-4"
        >
          <ArrowLeft className="w-4 h-4 mr-2" />
          Back to Dashboard
        </Link>
        <h1 className="text-3xl font-bold text-secondary-900 mb-2">
          Resume Analysis
        </h1>
        <p className="text-secondary-600">
          {resume.originalFileName}
        </p>
      </div>

      {/* Score Section */}
      {resume.analysis?.score && (
        <div className="card mb-8">
          <div className="flex items-center justify-between">
            <div>
              <h2 className="text-xl font-semibold text-secondary-900 mb-2">
                Overall Score
              </h2>
              <p className="text-secondary-600">
                Based on skills, experience, and content quality
              </p>
            </div>
            <div className={`text-center p-4 rounded-lg ${getScoreBgColor(resume.analysis.score)}`}>
              <div className={`text-3xl font-bold ${getScoreColor(resume.analysis.score)}`}>
                {resume.analysis.score}/100
              </div>
              <div className="flex items-center justify-center mt-2">
                {[...Array(5)].map((_, i) => (
                  <Star
                    key={i}
                    className={`w-4 h-4 ${
                      i < Math.floor(resume.analysis.score / 20)
                        ? 'text-yellow-400 fill-current'
                        : 'text-gray-300'
                    }`}
                  />
                ))}
              </div>
            </div>
          </div>
        </div>
      )}

      <div className="grid lg:grid-cols-2 gap-8">
        {/* Skills Section */}
        <div className="card">
          <div className="flex items-center mb-4">
            <TrendingUp className="w-5 h-5 text-primary-600 mr-2" />
            <h2 className="text-xl font-semibold text-secondary-900">
              Skills Detected
            </h2>
          </div>
          {resume.analysis?.skills?.length > 0 ? (
            <div className="flex flex-wrap gap-2">
              {resume.analysis.skills.map((skill, index) => (
                <span
                  key={index}
                  className="px-3 py-1 bg-primary-100 text-primary-700 rounded-full text-sm font-medium"
                >
                  {skill}
                </span>
              ))}
            </div>
          ) : (
            <p className="text-secondary-600">No skills detected in this resume.</p>
          )}
        </div>

        {/* Contact Information */}
        <div className="card">
          <div className="flex items-center mb-4">
            <User className="w-5 h-5 text-primary-600 mr-2" />
            <h2 className="text-xl font-semibold text-secondary-900">
              Contact Information
            </h2>
          </div>
          <div className="space-y-3">
            {resume.analysis?.contact?.email && (
              <div className="flex items-center">
                <Mail className="w-4 h-4 text-secondary-400 mr-3" />
                <span className="text-secondary-700">{resume.analysis.contact.email}</span>
              </div>
            )}
            {resume.analysis?.contact?.phone && (
              <div className="flex items-center">
                <Phone className="w-4 h-4 text-secondary-400 mr-3" />
                <span className="text-secondary-700">{resume.analysis.contact.phone}</span>
              </div>
            )}
            {resume.analysis?.contact?.location && (
              <div className="flex items-center">
                <MapPin className="w-4 h-4 text-secondary-400 mr-3" />
                <span className="text-secondary-700">{resume.analysis.contact.location}</span>
              </div>
            )}
            {!resume.analysis?.contact?.email && !resume.analysis?.contact?.phone && !resume.analysis?.contact?.location && (
              <p className="text-secondary-600">No contact information detected.</p>
            )}
          </div>
        </div>
      </div>

      {/* Experience Section */}
      {resume.analysis?.experience?.length > 0 && (
        <div className="card mt-8">
          <h2 className="text-xl font-semibold text-secondary-900 mb-4">
            Experience Highlights
          </h2>
          <div className="space-y-3">
            {resume.analysis.experience.map((exp, index) => (
              <div key={index} className="flex items-start">
                <CheckCircle className="w-4 h-4 text-green-500 mr-3 mt-1 flex-shrink-0" />
                <p className="text-secondary-700">{exp}</p>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Education Section */}
      {resume.analysis?.education?.length > 0 && (
        <div className="card mt-8">
          <h2 className="text-xl font-semibold text-secondary-900 mb-4">
            Education Highlights
          </h2>
          <div className="space-y-3">
            {resume.analysis.education.map((edu, index) => (
              <div key={index} className="flex items-start">
                <CheckCircle className="w-4 h-4 text-blue-500 mr-3 mt-1 flex-shrink-0" />
                <p className="text-secondary-700">{edu}</p>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Summary Section */}
      {resume.analysis?.summary && (
        <div className="card mt-8">
          <h2 className="text-xl font-semibold text-secondary-900 mb-4">
            Resume Summary
          </h2>
          <p className="text-secondary-700 leading-relaxed">
            {resume.analysis.summary}
          </p>
        </div>
      )}

      {/* Suggestions Section */}
      {resume.analysis?.suggestions?.length > 0 && (
        <div className="card mt-8">
          <div className="flex items-center mb-4">
            <Lightbulb className="w-5 h-5 text-yellow-600 mr-2" />
            <h2 className="text-xl font-semibold text-secondary-900">
              Improvement Suggestions
            </h2>
          </div>
          <div className="space-y-3">
            {resume.analysis.suggestions.map((suggestion, index) => (
              <div key={index} className="flex items-start">
                <AlertCircle className="w-4 h-4 text-yellow-500 mr-3 mt-1 flex-shrink-0" />
                <p className="text-secondary-700">{suggestion}</p>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Raw Text Section (Collapsible) */}
      {resume.extractedText && (
        <div className="card mt-8">
          <details className="group">
            <summary className="cursor-pointer text-lg font-semibold text-secondary-900 mb-4">
              View Extracted Text
            </summary>
            <div className="mt-4 p-4 bg-secondary-50 rounded-lg">
              <pre className="text-sm text-secondary-700 whitespace-pre-wrap font-sans">
                {resume.extractedText}
              </pre>
            </div>
          </details>
        </div>
      )}
    </div>
  );
};

export default ResumeAnalysis;
