import React from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { Upload, BarChart3, Lightbulb, Shield, Zap, CheckCircle, Star } from 'lucide-react';

const Home = () => {
  const { user } = useAuth();

  const features = [
    {
      icon: <Upload className="w-8 h-8" />,
      title: 'Smart Upload',
      description: 'Drag & drop your resume'
    },
    {
      icon: <BarChart3 className="w-8 h-8" />,
      title: 'AI Analysis',
      description: 'Instant insights & scoring'
    },
    {
      icon: <Lightbulb className="w-8 h-8" />,
      title: 'Smart Tips',
      description: 'Personalized suggestions'
    },
    {
      icon: <Shield className="w-8 h-8" />,
      title: 'Secure',
      description: 'Your data is protected'
    }
  ];

  const stats = [
    { number: '95%', label: 'Accuracy Rate' },
    { number: '10K+', label: 'Resumes Analyzed' },
    { number: '4.9★', label: 'User Rating' }
  ];

  return (
    <div className="min-h-screen">
      {/* Hero Section */}
      <div className="gradient-bg relative overflow-hidden">
        <div className="absolute inset-0 bg-gradient-to-br from-primary-600/10 to-secondary-600/10"></div>
        <div className="relative max-w-6xl mx-auto px-4 py-20">
          <div className="text-center">
            {/* Badge */}
            <div className="inline-flex items-center gap-2 bg-white/20 backdrop-blur-sm rounded-full px-4 py-2 mb-8">
              <Star className="w-4 h-4 text-yellow-400" />
              <span className="text-sm font-medium text-white">AI-Powered Analysis</span>
            </div>

            {/* Main Heading */}
            <h1 className="text-5xl md:text-7xl font-bold text-white mb-6 leading-tight">
              Resume
              <span className="block text-primary-200">Analyzer</span>
            </h1>

            {/* Subtitle */}
            <p className="text-xl text-white/90 mb-10 max-w-2xl mx-auto">
              Upload your resume and get instant AI-powered insights
            </p>

            {/* CTA Buttons */}
            <div className="flex flex-col sm:flex-row gap-4 justify-center mb-16">
              {user ? (
                <Link to="/dashboard" className="bg-white text-primary-600 hover:bg-gray-100 font-semibold px-8 py-4 rounded-xl transition-all duration-200 transform hover:scale-105">
                  Go to Dashboard
                </Link>
              ) : (
                <>
                  <Link to="/register" className="bg-white text-primary-600 hover:bg-gray-100 font-semibold px-8 py-4 rounded-xl transition-all duration-200 transform hover:scale-105">
                    Get Started Free
                  </Link>
                  <Link to="/login" className="border-2 border-white/30 text-white hover:bg-white/10 font-semibold px-8 py-4 rounded-xl transition-all duration-200">
                    Sign In
                  </Link>
                </>
              )}
            </div>

            {/* Stats */}
            <div className="grid grid-cols-3 gap-8 max-w-md mx-auto">
              {stats.map((stat, index) => (
                <div key={index} className="text-center">
                  <div className="text-2xl font-bold text-white mb-1">{stat.number}</div>
                  <div className="text-sm text-white/70">{stat.label}</div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>

      {/* Features Section */}
      <div className="py-20 bg-white">
        <div className="max-w-6xl mx-auto px-4">
          <div className="text-center mb-16">
            <h2 className="text-4xl font-bold text-secondary-900 mb-4">
              Why Choose Us?
            </h2>
            <p className="text-lg text-secondary-600">
              Advanced AI technology for better results
            </p>
          </div>
          
          <div className="grid md:grid-cols-2 lg:grid-cols-4 gap-8">
            {features.map((feature, index) => (
              <div key={index} className="group">
                <div className="bg-gradient-to-br from-primary-50 to-secondary-50 rounded-2xl p-8 text-center transition-all duration-300 hover:shadow-xl hover:-translate-y-2">
                  <div className="inline-flex items-center justify-center w-16 h-16 bg-primary-100 rounded-2xl mb-6 text-primary-600 group-hover:bg-primary-200 transition-colors duration-300">
                    {feature.icon}
                  </div>
                  <h3 className="text-xl font-semibold text-secondary-900 mb-3">
                    {feature.title}
                  </h3>
                  <p className="text-secondary-600">
                    {feature.description}
                  </p>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* How It Works */}
      <div className="py-20 bg-secondary-50">
        <div className="max-w-6xl mx-auto px-4">
          <div className="text-center mb-16">
            <h2 className="text-4xl font-bold text-secondary-900 mb-4">
              How It Works
            </h2>
            <p className="text-lg text-secondary-600">
              Simple 3-step process
            </p>
          </div>
          
          <div className="grid md:grid-cols-3 gap-8">
            {[
              { step: '1', title: 'Upload', desc: 'Drag & drop your resume' },
              { step: '2', title: 'Analyze', desc: 'AI processes your content' },
              { step: '3', title: 'Results', desc: 'Get insights & suggestions' }
            ].map((item, index) => (
              <div key={index} className="text-center group">
                <div className="relative">
                  <div className="w-20 h-20 bg-primary-600 text-white rounded-full flex items-center justify-center text-2xl font-bold mx-auto mb-6 group-hover:scale-110 transition-transform duration-300">
                    {item.step}
                  </div>
                  {index < 2 && (
                    <div className="hidden md:block absolute top-10 left-full w-full h-0.5 bg-primary-200 transform -translate-y-1/2"></div>
                  )}
                </div>
                <h3 className="text-xl font-semibold text-secondary-900 mb-3">
                  {item.title}
                </h3>
                <p className="text-secondary-600">
                  {item.desc}
                </p>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* CTA Section */}
      <div className="py-20 bg-gradient-to-r from-primary-600 to-primary-700">
        <div className="max-w-4xl mx-auto px-4 text-center">
          <h2 className="text-4xl font-bold text-white mb-6">
            Ready to improve your resume?
          </h2>
          <p className="text-xl text-white/90 mb-10">
            Join thousands of professionals who trust our AI analysis
          </p>
          {user ? (
            <Link to="/dashboard" className="bg-white text-primary-600 hover:bg-gray-100 font-semibold px-10 py-4 rounded-xl transition-all duration-200 transform hover:scale-105 inline-flex items-center gap-2">
              <Zap className="w-5 h-5" />
              Analyze Your Resume
            </Link>
          ) : (
            <Link to="/register" className="bg-white text-primary-600 hover:bg-gray-100 font-semibold px-10 py-4 rounded-xl transition-all duration-200 transform hover:scale-105 inline-flex items-center gap-2">
              <Zap className="w-5 h-5" />
              Start Free Analysis
            </Link>
          )}
        </div>
      </div>
    </div>
  );
};

export default Home;
